import React, { Component } from "react";
import axios from 'axios';
import Jumbotron from "./components/Jumbotron";
import Nav from "./components/Nav";
import Input from "./components/Input";
import Button from "./components/Button";
// import API from "./utils/API";
// import { RecipeList, RecipeListItem } from "./components/RecipeList";
import { Container, Row, Col } from "./components/Grid";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      stockSearch: ""
    }
  }

  // componentDidMount() {
  //   this.stockPrice(this.state.stockSearch);
  // }
  stockPrice = query => {

    var pointArray = [];
    var url = "https://www.alphavantage.co/query?function=TIME_SERIES_DAILY&symbol="
    // var symbol = "MSFT";
    var api_key = "&apikey=0RZS2TDL048HER8Y"
    axios.get(url+query+api_key)
      .then(function (response) {
        // console.log(response.data)
        // console.log(response.data["Time Series (Daily)"]["2018-09-26"]["4. close"]);
        var end = new Date();
        var dateArray = [];
        var stockArray = [];
        for (var d = new Date("2019-02-19"); d <= end; d.setDate(d.getDate() + 1)) {
          // console.log(d);
          // console.log(d.getDay());
          if (d.getDay() === 5) {
            console.log("Saturday")
          } else if (d.getDay() === 6) {
            console.log("Sunday");
          }
          // else if(moment().isHoliday(d) === true){
          //   console.log("Holiday");
          // }
          else {
            dateArray.push(new Date(d).toISOString().slice(0, 10));
          }
        }

        // console.log(response.data["Time Series (Daily)"][dateArray[0]]["4. close"]);
        // stockArray.push(response.data["Time Series (Daily)"][new Date(d).toISOString().slice(0,10)]["4. close"]);
        // console.log(dateArray);
        for (var i = 0; i < dateArray.length - 1; i++) {
          stockArray.push(parseFloat(response.data["Time Series (Daily)"][dateArray[i]]["4. close"]));
        }
        console.log(stockArray);
        console.log(Math.max(...stockArray));
        console.log(Math.min(...stockArray));
        for (var a = 0; a < stockArray.length; a++) {
          var p = stockArray[a];
          // console.log(y);
          // data.push({a,p});
          pointArray.push({ x: a, y: p });
        }

        console.log(pointArray);
        console.log(pointArray.length);
      }).then(
        this.setState({
          data: [
            {
              color: "steelblue",
              points: pointArray
            }
          ]
        })

        // this.setState(prevState => ({
        //   data: prevState.data.push({color:"steelblue", points:pointArray})
        // }))
      ).then(
        console.log("done")
      )
  }


  handleInputChange = event => {
    // Destructure the name and value properties off of event.target
    // Update the appropriate state
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };


  handleFormSubmit = event => {
    // When the form is submitted, prevent its default behavior, get recipes update the recipes state
    event.preventDefault();
    this.stockPrice(this.state.stockSearch)
      .then(res => {
        console.log(res.data);
        this.setState({ data: res.data });
      })
      .catch(err => console.log(err));
  };


  render() {
    const { data } = this.state;

    if (data.length > 0) {
      return (
        <div>
          <Nav />
          <Jumbotron />
          <Container>
            <Row>
              <Col size="md-12">
                <form>
                  <Container>
                    <Row>
                      <Col size="xs-9 sm-10">
                        <Input
                          name="stockSearch"
                          value={this.state.stocks}
                          onChange={this.handleInputChange}
                          placeholder="Search For a Stock"
                        />
                      </Col>
                      <Col size="xs-3 sm-2">
                        <Button
                          onClick={this.handleFormSubmit}
                          type="success"
                          className="input-lg"
                        >
                          Search
                        </Button>
                      </Col>
                    </Row>
                  </Container>
                </form>
              </Col>
            </Row>
            <Row>
              {/* <Col size="xs-12">
                {!this.state.recipes.length ? (
                  <h1 className="text-center">No Recipes to Display</h1>
                ) : (
                  <RecipeList>
                    {this.state.recipes.map(recipe => {
                      return (
                        <RecipeListItem
                          key={recipe.title}
                          title={recipe.title}
                          href={recipe.href}
                          ingredients={recipe.ingredients}
                          thumbnail={recipe.thumbnail}
                        />
                      );
                    })}
                  </RecipeList>
                )}
              </Col> */}
            </Row>
          </Container>
        </div>
      );
    } else {
      return null;
    }
  }
}

export default App;
